<script>
    document.addEventListener('DOMContentLoaded', function() {
        // Kiểm tra URL để đảm bảo rằng chỉ chạy trên trang quiz.
        if (window.location.href.includes('/mod/quiz/attempt.php')) {
            // Thêm HTML tùy chỉnh vào trang
            const customHtml = '<div class="custom-message" style="margin-bottom: 20px;"><strong></strong> Times played: 2.</div>';
            document.querySelector('body').insertAdjacentHTML('afterbegin', customHtml);

            // Logic xử lý audio
            const audioElements = document.querySelectorAll('audio');

            audioElements.forEach((audio) => {
                // Hide the default audio controls
                audio.controls = false;

                // Variables to track play count
                let playCount = 0;
                const maxPlays = 2;

                // Create a play button
                const playButton = document.createElement('button');
                playButton.textContent = 'Play';
                playButton.style.cssText = 'padding: 10px 15px; background-color: #4CAF50; color: white; border: none; border-radius: 5px; cursor: pointer; margin: 10px 0;';

                // Create a play count display
                const playCountDisplay = document.createElement('div');
                playCountDisplay.textContent = `Số lần đã nghe: ${playCount}/${maxPlays}`;
                playCountDisplay.style.cssText = 'margin: 10px 0; font-weight: bold; color: #333;';

                // Insert the play button and play count display before the audio element
                audio.parentNode.insertBefore(playButton, audio);
                audio.parentNode.insertBefore(playCountDisplay, audio);

                // Event listener for the play button
                playButton.addEventListener('click', function() {
                    if (playCount < maxPlays) {
                        audio.play();
                        playCount++;
                        playCountDisplay.textContent = `Số lần đã nghe: ${playCount}/${maxPlays}`;
                        playButton.disabled = true; // Disable play button while playing
                        playButton.style.backgroundColor = '#ccc'; // Change button color when disabled

                        // Re-enable the play button when audio ends
                        audio.addEventListener('ended', function() {
                            if (playCount < maxPlays) {
                                playButton.disabled = false;
                                playButton.style.backgroundColor = '#4CAF50'; // Reset button color
                            }
                        });
                    } else {
                        alert('Bạn đã đạt giới hạn số lần nghe cho file âm thanh này.');
                        playButton.disabled = true;
                        playButton.style.backgroundColor = '#ccc'; // Change button color when disabled
                    }
                });
            });
        }
    });
</script>
