<!-- Bắt đầu thêm mã JavaScript Hide/Show Password -->
<!-- Thêm trường nhập mật khẩu và icon con mắt -->
<style>
    /* CSS để căn chỉnh biểu tượng con mắt nằm bên trong trường nhập */
    .password-container {
        position: relative;
        width: 100%;
    }

    .password-container input {
        width: 100%;
        padding-right: 40px; /* Đảm bảo có khoảng trống cho biểu tượng con mắt */
    }

    .password-container .toggle-password {
        position: absolute;
        top: 50%;
        right: 10px;
        transform: translateY(-50%);
        cursor: pointer;
    }

    .toggle-password i {
        font-size: 1.2em;
        color: #888;
    }
</style>
<script>
    document.addEventListener('DOMContentLoaded', function() {
        const passwordField = document.querySelector('input[name="password"]');
        if (passwordField) {
            // Tạo một icon con mắt nằm trong thanh nhập
            const passwordContainer = document.createElement('div');
            passwordContainer.classList.add('password-container');

            // Chèn trường mật khẩu vào trong container
            passwordField.parentNode.insertBefore(passwordContainer, passwordField);
            passwordContainer.appendChild(passwordField);

            // Tạo icon con mắt
            const eyeIcon = document.createElement('span');
            eyeIcon.classList.add('toggle-password');
            eyeIcon.innerHTML = '<i class="fa fa-eye"></i>'; // Icon con mắt

            // Chèn icon con mắt vào container
            passwordContainer.appendChild(eyeIcon);

            // Thêm sự kiện click cho icon để ẩn/hiện mật khẩu
            eyeIcon.addEventListener('click', function() {
                const type = passwordField.getAttribute('type') === 'password' ? 'text' : 'password';
                passwordField.setAttribute('type', type);

                // Thay đổi giữa icon con mắt mở và đóng
                if (type === 'password') {
                    eyeIcon.innerHTML = '<i class="fa fa-eye"></i>';
                } else {
                    eyeIcon.innerHTML = '<i class="fa fa-eye-slash"></i>';
                }
            });
        }
    });
</script></body></html>